void set_keyboard_logo(enum logo_type new_logo) {
    current_logo = new_logo;
}

enum logo_type get_keyboard_logo(void) {
    return current_logo;
}

void set_keyboard_logo_to_dvorak(void) {
    current_logo = _DVORAK_LOGO;
}

void set_keyboard_logo_to_qwerty(void) {
    current_logo = _QWERTY_LOGO;
}

void set_keyboard_logo_to_plover(void) {
    current_logo = _PLOVER_LOGO;
}

const uint8_t render_keyboard_logo(uint8_t line) {
    oled_set_cursor(0, line);
    // Currently the state of left->right sides of
    // keyboard not sent so can't react to key press
    // events...
    switch (current_logo) {
        case _DVORAK_LOGO:
            oled_write_raw_P(lily_logo, LOGO_SIZE);
            break;
        case _QWERTY_LOGO:
            oled_write_raw_P(lily_logo, LOGO_SIZE);
            break;
        case _PLOVER_LOGO:
            oled_write_raw_P(lily_logo, LOGO_SIZE);
            break;
        default:
            break;
    }
    const uint8_t end_line = line + 4;
    return end_line;
}
